generated.css:
body {
  margin: 0;
  background-color: #efefef;
  background-image: url("https://wallpapertag.com/wallpaper/full/4/d/9/155959-super-mario-world-background-1920x1080-for-iphone.jpg");
  background-size: cover;
  background-repeat: no-repeat;
  overflow: hidden;
}

.hero {
  width: 150px;
  height: 150px;
  background-image: url("https://supermarioparty.nintendo.com/assets/img/global/logo-smp.png");
  background-size: contain;
  background-repeat: no-repeat;
}

h1 {
  margin-top: 10px;
  margin-bottom: 0px;
}

.question-box {
  font-family: "Staatliches";
  font-size: 40px;
  color: white;
  margin: 0px;
}

.answer {
  width: 200px;
  height: 200px;
  background-size: cover;
  font-size: 45px;
  color: #eb001d;
  text-transform: uppercase;
  letter-spacing: 1px;
  font-family: "Staatliches";
  background-color: white;
}

.container {
  width: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  text-align: center;
}

.buttons {
  width: 100%;
  display: flex;
  justify-content: space-around;
}

button {
  width: 200px;
  background-color: red;
  color: white;
  font-family: "Staatliches";
  font-size: 30px;
}

.walkway {
  position: absolute;
  bottom: 0px;
  width: 100%;
  height: 100px;
  transition: transform 60s;
  transform: translateX(1400px);
}

.mario {
  float: left;
  height: 80px;
  width: 80px;
  background-image: url("https://vignette.wikia.nocookie.net/lawl-stadium/images/4/4d/8-Bit_Mario.png/revision/latest?cb=20130907061200");
  background-size: contain;
  background-repeat: no-repeat;
  margin-top: 21px;
}
